import styled from "styled-components";

export const Container = styled.div`
  background-image: url(${(props) => props.backgroundImage});
  height: 150vh;
  max-width: 280px;
  min-width: 80px;
  color: white;
  width: 20%;
`;
export const Image = styled.img`
  display: block;
  margin-left: auto;
  margin-right: auto;
  width: 150px;
  height: 100px;
  border-radius: 50%;
  margin-top: 10px;
`;
export const sidebarHeader = styled.h3`
  font-weight: 500;
  text-align: center;
  padding: 10px 0;
  font-family: "Pappins", "san-serif";
  &:after {
    content: "";
    border: 1px solid rgba(225, 112, 85);
    display: block;
    margin: 5px 4px 4px;
  }
`;
export const MenuItem = styled.div`
  padding: 6px 20px;
  font-family: "Poppins", "san-serif";
  font-size: 12px;
  font-weight: 500;
  &:after {
    content: "";
    border: 1px solid
      ${p => p.isselected ? 'rgb(225, 225, 225)' : 'rgb(74, 98, 196)'};
    display: block;
    margin: 5px 0 4px;
  }
`;
export const menuContainer = styled.div``;
export const Icon = styled.span`

padding-right: 20px;
`;
export const Text = styled.p`
  color: ${(p) => (p.isselected ? "rgb(225,225,225)" : "rgb(135, 152, 219)")};
  display: inline;

`;
