import {ADD_CUSTOMER} from '../action'
const initialstate = {  
    employees: [  
        { id: 1, fname: "Asif", 
        lname: "bsit",
        email: "bsit@gmail.com",
        phone: "bsit",
        address: "bsit",
        city: "bsit",
        state: "bsit",
        zip: "bsit",
        country: "bsit",
        company: "bsit",
        comments:"comments"
     },  
         ]  
}; 


const customer = (state = initialstate, action) => {  
    switch (action.type) {  
        case GET_CUSTOMER: 
        let newState = {...state}
            return newState
        case 'ADD_EMPLOYEE':    
            return {    
                ...state,    
                employees: state.employees.concat(action.payload)    
            };  
        case 'DELETE_EMPLOYEE':    
            return {    
                ...state,    
                employees: state.employees.filter(item=>item.id!==action.payload)    
            };  
        case 'EDIT_EMPLOYEE':    
            return {    
                ...state,    
                employees: state.employees.map((content)=>content.id ===action.payload.id ? {...content, employeeName:action.payload.employeeName,employeeDepartment : action.payload.employeeDepartment}:content)    
            };  
        default:  
            return state;  
    }  
};  

export default customer;
