import styled from "styled-components";
import { Link as link} from "react-router-dom";

export const Container = styled.div`
grid-area:sidebar;

`;
export const Navbar = styled.div`
  height:80px;
  display:flex;
  align-items:center;

  background:#060b26;
  color:#fff;
`;
export const NavMenu = styled.div`
  background:#000;
  width:250px;
  height:100vh;
  display:flex;
  justify-content:center;
  position:fixed;
  top:0;
  /* left:-100%; */
  transition:850ms;
`;
export const MenuItem = styled.li`
display:flex;
margin-left:2rem;
align-items:center;
padding:8px 0px 8px 0px;
list-style:none;
height:20px;
&:hover{
    background:#1a83ff;
    padding:8px 0px 8px 0px;
  }
`;



export const Link = styled(link)`
 margin-left:2rem;
 font-size:2rem;
 background:none;


`;
export const NavMenuItem = styled.ul`
  width:100%;
  margin-right:30px;
  margin-left:20px;
`;
export const NavToogle = styled.li`
background:#060b26;
  width:80%;
  height:80px;
  display:flex;
  justify-content:center;
  align-items:center;

`

export const menuContainer = styled.div``;
export const iconText = styled.i`
  color: ${(p) => (p.isselected ? "rgba(225,225,225)" : "rgba(135, 152, 219)")};


`;
export const Icon = styled.span`
  text-decoration:none;
  color:#fff;
  width:20%;


  border-radius:4px;

`;

export const Text = styled.span`
  color: ${(p) => (p.isselected ? "rgba(225,225,225)" : "rgba(135, 152, 219)")};
  width:30px;
  text-decoration:none;

`;
