import styled from 'styled-components'

export const Container = styled.div`
    background-image:linear-gradient((0deg,url(${props=>props.backgroundImage})));
    height:100vh;
    max-width:280px;
    min-width:80px;
    color:white;
    width:20%;
    display:flex;
    justify-content:'center';
    align-items:'center';
`
export const Image = styled.img`
    margin:40px;
    width:100px;
    height:100px;
    border-radius:50;

`
