import React, { Component } from "react";
import * as customer from "./Customer.style";
import {AddCustomer} from '../../action/customer';
import {connect} from 'react-redux';
import {generateId} from '../../utils/generateId';
import {Redirect} from 'redux'
import ButtonText from "../../components/ButtonText/ButtonText";
class Customer extends Component {
  state = {
    id:'',
    fname: "",
    lname: "",
    email: "",
    phone: "",
    address: "",
    city: "",
    state: "",
    zip: "",
    country: "",
    company: "",
    comments: "",
    error: "",
  };
  handleInput = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  createOrder = (e) => {
    e.preventDefault();
    alert(this.state.fname)
    const order = {
      id:generateId(),
      fname: this.state.fname,
      lname: this.state.lname,
      email: this.state.email,
      phone: this.props.phone,
      address: this.props.address,
      city: this.props.city,
      zip: this.props.zip,
      country: this.props.country,
      company: this.props.company,
      comments: this.props.comments,
    };
    this.props.AddCustomer(order)

  };
  handleSubmit = () => {
    alert("asif");
  };
  render() {
    console.log(this.state);
    return (
      <customer.Container>
        <customer.Title>Customer Information</customer.Title>
        <customer.Form>
          <customer.InputContainer>
            <customer.Label>First Name :</customer.Label>
            <customer.Input
              name="fname"
              value={this.state.fname}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Last Name :</customer.Label>

            <customer.Input
              name="lname"
              value={this.state.lname}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Email :</customer.Label>

            <customer.Input
              name="email"
              value={this.state.email}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Phone :</customer.Label>

            <customer.Input
              name="phone"
              value={this.state.phone}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Address :</customer.Label>

            <customer.Input
              name="address"
              value={this.state.address}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>City :</customer.Label>

            <customer.Input
              name="city"
              value={this.state.city}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>State :</customer.Label>

            <customer.Input
              name="state"
              value={this.state.state}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Zip Code :</customer.Label>

            <customer.Input
              name="zip"
              value={this.state.zip}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Country :</customer.Label>

            <customer.Input
              name="country"
              value={this.state.country}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Company :</customer.Label>

            <customer.Input
              name="company"
              value={this.state.company}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Comments :</customer.Label>

            <customer.comments
              rows="5"
              name="comments"
              value={this.state.comments}
              onChange={this.handleInput}
            />
          </customer.InputContainer>
          <customer.Button>
            <ButtonText
              disabled={this.state.fname === "" || this.state.lname === ""}
              children="Submit"
              onClick={this.createOrder}
            >
              Submit
            </ButtonText>
          </customer.Button>
        </customer.Form>
      </customer.Container>
    );
  }
}
const mapStateToProps = (state)=>{
  return{
    customer:state.customers
  }
}
export default connect(mapStateToProps,{AddCustomer})(Customer)
