import React, { Component } from "react";
import * as customer from "./Customer.style";
import ButtonText from '../../components/ButtonText/ButtonText'
export default class Customer extends Component {
  state = {
    fname:'',
    lname:'',
    email:'',
    phone:'',
    address:'',
    city:'',
    state:'',
    zip:'',
    country:'',
    comments:''
  }
handleFName = ()=>{
  this.setState({
    fname:e.target.value
  })
}
handleLName = ()=>{
  this.setState({
    lname:e.target.value
  })
}
handleEmail = ()=>{
  this.setState({
    email:e.target.value
  })
}
handlePhone = ()=>{
  this.setState({
    phone:e.target.value
  })
}
handleAddress = ()=>{
  this.setState({
    address:e.target.value
  })
}
handleCity = ()=>{
  this.setState({
    city:e.target.value
  })
}
handleState = ()=>{
  this.setState({
    state:e.target.value
  })
}
handleZip = ()=>{
  this.setState({
    zip:e.target.value
  })
}
handleCountry = ()=>{
  this.setState({
    country:e.target.value
  })
}
handleComments = ()=>{
  this.setState({
    comments:e.target.value
  })
}

  render() {

    return (
      <customer.Container>
        <customer.Title>Customer Information</customer.Title>
        <customer.Form>
          <customer.InputContainer>
            <customer.Label>First Name :</customer.Label>
            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Last Name :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Email :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Phone :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Address :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>City :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>State :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Zip Code :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Country :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Company :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.InputContainer>
            <customer.Label>Comments :</customer.Label>

            <customer.Input />
          </customer.InputContainer>
          <customer.Button>
          <ButtonText children="Submit">Submit</ButtonText>
          </customer.Button>

        </customer.Form>
      </customer.Container>
    );
  }
}
