import styled from 'styled-components';

export const Container = styled.button`
width:100px;
height:40px;
background:rgb(22, 47, 127);
font-size:14px;
font-weight:600;
display:flex;
justify-content:center;
align-items:center;
outline:none;
border:1px solid #757575;
border-radius:3px;
color:#fff;
&:hover {
    opacity: .8;
  }

`
