import styled from "styled-components";
import { Link as link } from "react-router-dom";

export const Container = styled.div`
  grid-area: sidebar;
`;
export const Navbar = styled.div`
  height: 100%;
  display: flex;
  align-items: center;
  background: #373942;
  color: #fff;
`;
export const NavMenu = styled.div`
  background: #373942;
  width: 250px;
  height: 100vh;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  /* left:-100%; */
  transition: 850ms;
`;
export const MenuItem = styled.li`
  display: flex;
  margin-left: 1rem;
  align-items: center;
  padding: 8px 0px 8px 0px;
  list-style: none;

  &:hover {
    background: #000;
    border-radius: 10px;
    color: #fff;
  }
`;

export const Link = styled(link)`
  margin-left: 2rem;
  font-size: 2rem;
  background: none;

`;
export const NavMenuItem = styled.ul`
  width: 100%;
  margin-right: 30px;
`;
export const NavToogle = styled.li`
  background: #060b26;
  width: 40%;
  margin-left: 30px;
  padding: 20px;
  list-style: none;
  margin-top:20px;
`;
export const TooglLink = styled(link)`
  text-align: center;
  color: white;
  padding-left: 20px;
`;

export const menuContainer = styled.div``;
export const iconText = styled.i`
  color: ${(p) => (p.isselected ? "rgba(225,225,225)" : "#757575")};
`;
export const Icon = styled.span`
  text-decoration: none;
  color: #fff;
  width: 20%;
  border-radius: 10px;
  padding: 0 10px;
  &:hover {
    color: #fff;
  }
`;

export const Text = styled.span`
  color: ${(p) => (p.isselected ? "rgba(225,225,225)" : "#757575")};
  width: 30px;
  text-decoration: none;

  &:hover {
    color: #fff;
  }
`;
