import styled from "styled-components";
import { Link as link } from "react-router-dom";

export const Container = styled.div`
  grid-area: sidebar;
 height:100%;
 background:#F8F8F8;

`;
export const Navbar = styled.div`
  display: flex;
  align-items: center;
  background: #F8F8F8;
  color: #fff;
`;
export const NavMenu = styled.div`
  background: #373942;
  width: 22%;
  height: 100vh;
  display: flex;
  justify-content: center;
  position: fixed;
  left:${(p) => (p.sidebar ? '-100%' : "")}; ;
  transition: 850ms;
  @media (max-width: 800px) {
    font-size:10px;
    width: 100px;
   grid-area:""
  }
  @media (max-width: 400px) {
    font-size:10px;
    width: 100px;
   grid-area:""
  }
`;
export const MenuItem = styled.li`
  display: flex;
  margin-left: 1rem;
  align-items: center;
  padding: 8px 0px 8px 0px;
  list-style: none;

  &:hover {
    background: #000;
    border-radius: 10px;
    color: #fff;
  }

  @media (max-width: 980px) {
    font-size:10px;
    width:100px;
    margin-right: 80px;
    display:flex;
    justify-content:center;
    align-items:center;
    padding:8px 0 8px 0px
