export const menuContainer = styled.div``;
export const iconText = styled.i`
  color: ${(p) => (p.isselected ? "#757575" : "#fff")};
  &:hover {
    color: #fff;
  }
`;
export const Icon = styled.span`
  text-decoration: none;
  color: #fff;
  width: 9%;
  border-radius: 10px;
  padding: 0 10px;
  @media (max-width: 800px) {
    font-size:10px;

  }
`;

export const Text = styled.span`
  color: ${(p) => (p.isselected ? "#757575" : "#fff")};
