import { SET_USER } from "../action/users";
const users = (state = [], action) => {
  alert(action.payload);
  switch (action.type) {
    case SET_USER:
      return {
        ...state,
        user :action.payload,
        loggedIn: true,
      };
    default :
    return {state}
  }
};

export default users;
